          vx2: (Math.random() - 0.5) * 0.2,
          vy2: (Math.random() - 0.5) * 0.2,
          opacity: Math.random() * 0.15 + 0.05
        });
      }

      // Create rotating squares
      const squareCount = Math.floor((canvas.width * canvas.height) / 100000);
      for (let i = 0; i < squareCount; i++) {
        squaresRef.current.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          size: Math.random() * 25 + 15,
          rotation: Math.random() * Math.PI * 2,
          rotationSpeed: (Math.random() - 0.5) * 0.015,
          vx: (Math.random() - 0.5) * 0.3,
          vy: (Math.random() - 0.5) * 0.3,
          opacity: Math.random() * 0.2 + 0.08
        });
      }
    };

    // Handle mouse movement
    const handleMouseMove = (e) => {
      const rect = canvas.getBoundingClientRect();
      mouseRef.current = {
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      };
    };

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw and update lines
      linesRef.current.forEach(line => {
        // Update line positions
        line.x1 += line.vx1;
        line.y1 += line.vy1;
        line.x2 += line.vx2;
        line.y2 += line.vy2;

        // React to mouse
        const dx1 = mouseRef.current.x - line.x1;
        const dy1 = mouseRef.current.y - line.y1;
        const dist1 = Math.sqrt(dx1 * dx1 + dy1 * dy1);
        if (dist1 < 150) {
          line.x1 -= dx1 / dist1 * 0.3;
          line.y1 -= dy1 / dist1 * 0.3;
        }

        const dx2 = mouseRef.current.x - line.x2;
        const dy2 = mouseRef.current.y - line.y2;
        const dist2 = Math.sqrt(dx2 * dx2 + dy2 * dy2);
        if (dist2 < 150) {
          line.x2 -= dx2 / dist2 * 0.3;
          line.y2 -= dy2 / dist2 * 0.3;
        }

        // Wrap around edges
        if (line.x1 < 0) line.x1 = canvas.width;
        if (line.x1 > canvas.width) line.x1 = 0;
        if (line.y1 < 0) line.y1 = canvas.height;
        if (line.y1 > canvas.height) line.y1 = 0;
        if (line.x2 < 0) line.x2 = canvas.width;
        if (line.x2 > canvas.width) line.x2 = 0;
        if (line.y2 < 0) line.y2 = canvas.height;
        if (line.y2 > canvas.height) line.y2 = 0;

        // Draw line
        ctx.beginPath();
        ctx.moveTo(line.x1, line.y1);
        ctx.lineTo(line.x2, line.y2);
        ctx.strokeStyle = `rgba(250, 112, 112, ${line.opacity})`;
        ctx.lineWidth = 1;
        ctx.stroke();
      });

