        // Update square position and rotation
        square.x += square.vx;
        square.y += square.vy;
        square.rotation += square.rotationSpeed;

        // React to mouse
        const dx = mouseRef.current.x - square.x;
        const dy = mouseRef.current.y - square.y;
        const dist = Math.sqrt(dx * dx + dy * dy);
        if (dist < 200) {
          square.x -= dx / dist * 1;
          square.y -= dy / dist * 1;
          square.rotationSpeed += (Math.random() - 0.5) * 0.01;
        }

        // Wrap around edges
        if (square.x < -square.size) square.x = canvas.width + square.size;
        if (square.x > canvas.width + square.size) square.x = -square.size;
        if (square.y < -square.size) square.y = canvas.height + square.size;
        if (square.y > canvas.height + square.size) square.y = -square.size;

        // Draw square
        ctx.save();
        ctx.translate(square.x, square.y);
        ctx.rotate(square.rotation);
        ctx.strokeStyle = `rgba(250, 112, 112, ${square.opacity})`;
        ctx.lineWidth = 1.5;
        ctx.strokeRect(-square.size / 2, -square.size / 2, square.size, square.size);
        
        // Draw diagonal lines
        ctx.beginPath();
        ctx.moveTo(-square.size / 2, -square.size / 2);
        ctx.lineTo(square.size / 2, square.size / 2);
        ctx.moveTo(square.size / 2, -square.size / 2);
        ctx.lineTo(-square.size / 2, square.size / 2);
        ctx.strokeStyle = `rgba(250, 112, 112, ${square.opacity * 0.4})`;
        ctx.lineWidth = 1;
        ctx.stroke();
        
        ctx.restore();
      });

      // Draw and update particles
      particlesRef.current.forEach(particle => {
        // Update particle position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // React to mouse
        const dx = mouseRef.current.x - particle.x;
        const dy = mouseRef.current.y - particle.y;
        const dist = Math.sqrt(dx * dx + dy * dy);
        if (dist < 120) {
          particle.x -= dx / dist * 1.5;
          particle.y -= dy / dist * 1.5;
        }

        // Wrap around edges
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(250, 112, 112, 0.25)';
        ctx.fill();
      });

      // Draw connections between nearby particles
      particlesRef.current.forEach((p1, i) => {
        particlesRef.current.slice(i + 1).forEach(p2 => {
          const dx = p1.x - p2.x;
          const dy = p1.y - p2.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < 100) {
            ctx.beginPath();
            ctx.moveTo(p1.x, p1.y);
            ctx.lineTo(p2.x, p2.y);
            ctx.strokeStyle = `rgba(250, 112, 112, ${0.1 * (1 - dist / 100)})`;
            ctx.lineWidth = 0.5;
            ctx.stroke();
          }
        });
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    // Initialize
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    window.addEventListener('mousemove', handleMouseMove);
    
    // Delay animation start slightly for better performance
    setTimeout(() => animate(), 100);

    // Cleanup
    return () => {
      window.removeEventListener('resize', resizeCanvas);
      window.removeEventListener('mousemove', handleMouseMove);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return <canvas ref={canvasRef} className="content-background" />;
};

export default ContentBackground;
