import React, { useEffect, useRef } from 'react';
import './AnimatedBackground.css';

const AnimatedBackground = () => {
  const canvasRef = useRef(null);
  const waveRef = useRef({ time: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      waveRef.current.time += 0.01;
      const time = waveRef.current.time;

      // Grid dimensions
      const cols = 25;
      const rows = 15;
      const cellWidth = canvas.width / (cols - 1);
      const cellHeight = canvas.height / (rows - 1);

      // Create wave grid points
      const points = [];
      for (let row = 0; row < rows; row++) {
        points[row] = [];
        for (let col = 0; col < cols; col++) {
          const x = col * cellWidth;
          const baseY = row * cellHeight;
          
          // Create flowing wave pattern
          const wave1 = Math.sin(col * 0.3 + time) * 20;
          const wave2 = Math.cos(row * 0.2 + time * 0.8) * 15;
          const wave3 = Math.sin((col + row) * 0.15 + time * 1.2) * 10;
          
          const y = baseY + wave1 + wave2 + wave3;
          
          points[row][col] = { x, y };
        }
      }

      // Draw wireframe
      ctx.strokeStyle = 'rgba(254, 253, 237, 0.2)';
      ctx.lineWidth = 1;

      // Draw horizontal lines
      for (let row = 0; row < rows; row++) {
        ctx.beginPath();
        for (let col = 0; col < cols; col++) {
          const point = points[row][col];
          if (col === 0) {
            ctx.moveTo(point.x, point.y);
          } else {
            ctx.lineTo(point.x, point.y);
          }
        }
        ctx.stroke();
      }

      // Draw vertical lines
      for (let col = 0; col < cols; col++) {
        ctx.beginPath();
        for (let row = 0; row < rows; row++) {
          const point = points[row][col];
          if (row === 0) {
            ctx.moveTo(point.x, point.y);
          } else {
            ctx.lineTo(point.x, point.y);
          }
        }
        ctx.stroke();
      }

      // Draw nodes at intersections
      ctx.fillStyle = 'rgba(254, 253, 237, 0.4)';
      for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
          const point = points[row][col];
          ctx.beginPath();
          ctx.arc(point.x, point.y, 2, 0, Math.PI * 2);
          ctx.fill();
        }
      }

      animationFrameId = requestAnimationFrame(animate);
    };

    // Initialize
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    animate();

    // Cleanup
    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return <canvas ref={canvasRef} className="animated-background" />;
};

export default AnimatedBackground;
